import { useState } from 'react';
import Input from '../../Components/InputReuseable/Input';
import './login.css';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [usernameErr, setUsernameErr] = useState(false);
  const [passwordErr, setpasswordErr] = useState(false);

  const [formVal, setFormVal] = useState({
    username: {
      value: "",
      error: false,
    },
    password: {
      value: "",
      error: false,
    },
    formError: true,
  });

  const formFieldHandler = (event) => {
    const value = event.target.value;
    const name = event.target.name;
    console.log(value, name);
    let error = false;
    if (name === "username") {
      error = value.trim().length < 5;
    } else if (name === "password") {
      error = value.trim().length < 7;
    }
    setFormVal((prev) => {
      return {
        ...prev,
        [name]: {
          value,
          error,
        },
      };
    });
    setFormVal((prev) => {
      return { ...prev, formError: isFormError(prev) };
    });
  };


  const isFormError = ({ username, password }) => {
    return (
      username.error ||
      password.error ||
      username.value === "" ||
      password.value === ""
    );
  };

  const userNameHandler = (event) => {
    const inputVal = event.target.value;
    // if(inputVal.trim().length <= 3){
    //     setUsernameErr(true)
    // }
    // else
    // {
    //     setUsernameErr(false)
    // }
    setUsername(inputVal);
    setUsernameErr(inputVal.trim().length <= 3);
  };

  const passwordHandler = (event) => {
    const inputVal = event.target.value;
    setPassword(inputVal);
    setpasswordErr(inputVal.trim().length <= 5);
  };

  const loginFormHandler = (e) => {
    e.preventDefault();
    console.log("form value", formVal.username.value, formVal.password.value);
  };

  return (
    <div className="form-box">
      <form onSubmit={loginFormHandler}>
        <h2>Login Form</h2>
        <Input
          onChange={formFieldHandler}
          className={`${usernameErr ? 'error-field' : ''}`}
          field={{
            type: 'text',
            name: 'username',
            value: username,
            placeholder: 'Username',
            autoComplete: 'false',
          }}
        />
        <Input
          onChange={formFieldHandler}
          className={`${passwordErr ? 'error-field' : ''}`}
          field={{
            type: 'password',
            name: 'password',
            value: password,
            placeholder: 'Password',
            autoComplete: 'false',
          }}
        />
        <div className="submitBtn">
          <button
            type="submit"
            disabled={
              usernameErr ||
              passwordErr ||
              username.length === 0 ||
              password.length === 0
            }
          >
            Sign In
          </button>
        </div>
      </form>
    </div>
  );
};

export default Login;
