import React from 'react';

const Tick = () => {
    let formattedDate;
    setInterval((formattedDate) => {
        const currentDate = new Date();
        formattedDate = currentDate.toLocaleDateString('en-in',{
            day: 'numeric', month: 'numeric', year: 'numeric', hour: 'numeric', minute: 'numeric', second: 'numeric'
        })  
    }, 500);

  return (
    <div>Current Date: {formattedDate}</div>
  )
}

export default Tick;
