import React from 'react';

const Tick = () => {
  let formattedDate;
  setInterval(() => {
    const currentDate = new Date();
    formattedDate = currentDate.toLocaleDateString('en-in', {
      day: 'numeric',
      month: 'numeric',
      year: 'numeric',
      hour: 'numeric',
      minute: 'numeric',
      second: 'numeric',
    });
  }, 500);

  return <div>Current Date: {formattedDate.day}</div>;
};

export default Tick;
