import React, { useState } from 'react';

const Tick = () => {
    const [formattedDate, setFormattedDate] = useState('');
//   let formattedDate;
    setInterval(() => {
        const currentDate = new Date();
        const fDate = currentDate.toLocaleDateString('en-in', {
        day: 'numeric',
        month: 'numeric',
        year: 'numeric',
        hour: 'numeric',
        minute: 'numeric',
        second: 'numeric',
        });
        // console.log(formattedDate);
        setFormattedDate(fDate);
    }, 1000);
    console.log("Running",formattedDate);

  return <div>Current Date: {formattedDate}</div>;
};

export default Tick;
