  export default function Counter({initialCount}) {

    useReducer(reducer, initialState);

    return (
      <>
        <p>Count</p>
        <div className="btn">
            <button onClick={() => setCount({type: 'decrement'})}>-</button>
            <button onClick={() => setCount({type: 'increment'})}>+</button>
        </div>
      </>
    );
  }
